document.addEventListener('DOMContentLoaded', function() {
    // Set current year in footer
    document.getElementById('current-year').textContent = new Date().getFullYear();

    // Initialize products
    const products = [
        {
            name: "Fresh Whole Milk",
            image: "https://scene7.samsclub.com/is/image/samsclub/0007083650040_A?wid=400&hei=400",
            sizes: ["500ml - KSh 60", "1L - KSh 110"],
            description: ""
        },
        {
            name: "Low Fat Milk",
            image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSlOmYVmmgw1MNt4S1EtSJJlqgXi2r0K9CWyg&s",
            sizes: ["500ml - KSh 65", "1L - KSh 120"],
            description: ""
        },
        {
            name: "Flavored Milk",
            image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTOFewOSfR2uxW6fu0UF0gMU0TnmWRwMOSXvw&s",
            sizes: ["500ml - KSh 80"],
            description: "Strawberry/Vanilla/Chocolate"
        },
        {
            name: "Mala (Fermented Milk)",
            image: "data:image/jpeg;base64,",
            sizes: ["500ml - KSh 70", "1L - KSh 130"],
            description: ""
        }
    ];


    // Initialize locations
    const locations = [
        "Nairobi CBD",
        "Westlands",
        "Karen",
        "Thika Road",
        "Kasarani",
        "Langata"
    ];

    // Initialize gallery images
    const galleryImages = [
        { src: "data:image/jpeg;base64,", alt: "Our dairy farm in Kiambu" },
        { src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ4JbazKLPurpDmMKIhnMW1RWeD8XGatp4pNQ&s", alt: "Happy cows grazing" },
        { src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTXPfcHjOe6dlsTzRZjq8jbbAgcLt29QGsYeg&s", alt: "Milking process" },
        { src: "data:image/jpeg;base64,", alt: "Milk processing facility" },
        { src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRlBDlCZdkWK9_1u2aEH6zG-2SqeO1NRjMOsg&s", alt: "Fresh milk delivery" }
    ];

    // Display products
    const productGrid = document.querySelector('.product-grid');

    function displayProducts() {
        productGrid.innerHTML = '';
        products.forEach((product, index) => {
            const productCard = document.createElement('div');
            productCard.className = 'product-card';
            productCard.dataset.index = index;

            productCard.innerHTML = `
                <img src="${product.image}" alt="${product.name}">
                <h3>${product.name}</h3>
                ${product.description ? `<p>${product.description}</p>` : ''}
                ${product.sizes.map(size => `<p>${size}</p>`).join('')}
                <button class="btn add-to-cart">Add to Cart</button>
                <button class="btn secondary remove-product">Remove</button>
            `;

            productGrid.appendChild(productCard);
        });

        // Add event listeners to remove buttons
        document.querySelectorAll('.remove-product').forEach(button => {
            button.addEventListener('click', function() {
                const productIndex = this.parentElement.dataset.index;
                removeProduct(productIndex);
            });
        });

        // Add hover effects to product cards
        document.querySelectorAll('.product-card').forEach(card => {
            card.addEventListener('mouseenter', function() {
                this.style.transform = 'translateY(-5px) scale(1.02)';
                this.style.boxShadow = '0 10px 20px rgba(0, 0, 0, 0.1)';
            });

            card.addEventListener('mouseleave', function() {
                this.style.transform = '';
                this.style.boxShadow = '';
            });
        });
    }

    displayProducts();

    // Display locations
    const locationsList = document.getElementById('locations-list');
    locations.forEach(location => {
        const li = document.createElement('li');
        li.textContent = location;
        locationsList.appendChild(li);
    });

    // Hamburger menu toggle
    const hamburger = document.querySelector('.hamburger');
    const navLinks = document.querySelector('.nav-links');

    hamburger.addEventListener('click', function() {
        navLinks.classList.toggle('active');
    });

    // Dark mode toggle
    const themeToggle = document.getElementById('theme-toggle');
    themeToggle.addEventListener('click', function() {
        document.body.classList.toggle('dark-mode');

        // Change button text based on current mode
        if (document.body.classList.contains('dark-mode')) {
            themeToggle.textContent = 'Toggle Light Mode';
            localStorage.setItem('darkMode', 'enabled');
        } else {
            themeToggle.textContent = 'Toggle Dark Mode';
            localStorage.setItem('darkMode', 'disabled');
        }
    });

    // Check for saved dark mode preference
    if (localStorage.getItem('darkMode') === 'enabled') {
        document.body.classList.add('dark-mode');
        themeToggle.textContent = 'Toggle Light Mode';
    }

    // Add product button
    const addProductBtn = document.getElementById('add-product-btn');
    addProductBtn.addEventListener('click', function() {
        const newProduct = {
            name: "New Premium Milk",
            image: "data:image/jpeg;base64,",
            sizes: ["500ml - KSh 75", "1L - KSh 140"],
            description: "New to our collection!"
        };

        products.push(newProduct);
        displayProducts();
    });

    // Remove product function
    function removeProduct(index) {
        products.splice(index, 1);
        displayProducts();
    }

    // Toggle layout view
    const toggleLayoutBtn = document.getElementById('toggle-layout-btn');
    toggleLayoutBtn.addEventListener('click', function() {
        productGrid.classList.toggle('product-list');

        // Change button text based on current view
        if (productGrid.classList.contains('product-list')) {
            toggleLayoutBtn.textContent = 'Toggle Grid View';
        } else {
            toggleLayoutBtn.textContent = 'Toggle List View';
        }
    });

    // Shuffle products
    const shuffleBtn = document.getElementById('shuffle-products');
    shuffleBtn.addEventListener('click', function() {
        // Fisher-Yates shuffle algorithm
        for (let i = products.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [products[i], products[j]] = [products[j], products[i]];
        }
        displayProducts();
    });

    // Initialize gallery
    const galleryMainImage = document.getElementById('gallery-main-image');
    const galleryThumbnails = document.querySelector('.gallery-thumbnails');
    let currentImageIndex = 0;

    // Create thumbnails
    galleryImages.forEach((image, index) => {
        const thumbnail = document.createElement('img');
        thumbnail.src = image.src;
        thumbnail.alt = image.alt;
        thumbnail.className = 'gallery-thumbnail';
        if (index === 0) thumbnail.classList.add('active');

        thumbnail.addEventListener('click', function() {
            currentImageIndex = index;
            updateGallery();
        });

        galleryThumbnails.appendChild(thumbnail);
    });

    // Gallery navigation
    document.getElementById('prev-btn').addEventListener('click', function() {
        currentImageIndex = (currentImageIndex - 1 + galleryImages.length) % galleryImages.length;
        updateGallery();
    });

    document.getElementById('next-btn').addEventListener('click', function() {
        currentImageIndex = (currentImageIndex + 1) % galleryImages.length;
        updateGallery();
    });

    function updateGallery() {
        // Fade out current image
        galleryMainImage.style.opacity = 0;

        setTimeout(() => {
            galleryMainImage.src = galleryImages[currentImageIndex].src;
            galleryMainImage.alt = galleryImages[currentImageIndex].alt;
            galleryMainImage.style.opacity = 1;

            // Update active thumbnail
            document.querySelectorAll('.gallery-thumbnail').forEach((thumb, index) => {
                if (index === currentImageIndex) {
                    thumb.classList.add('active');
                } else {
                    thumb.classList.remove('active');
                }
            });
        }, 300);
    }

    // Auto-rotate gallery
    let galleryInterval = setInterval(() => {
        currentImageIndex = (currentImageIndex + 1) % galleryImages.length;
        updateGallery();
    }, 5000);

    // Pause auto-rotation on hover
    galleryMainImage.parentElement.addEventListener('mouseenter', () => {
        clearInterval(galleryInterval);
    });

    galleryMainImage.parentElement.addEventListener('mouseleave', () => {
        galleryInterval = setInterval(() => {
            currentImageIndex = (currentImageIndex + 1) % galleryImages.length;
            updateGallery();
        }, 5000);
    });

    // Tab functionality
    const tabButtons = document.querySelectorAll('.tab-btn');
    const tabPanels = document.querySelectorAll('.tab-panel');

    tabButtons.forEach(button => {
        button.addEventListener('click', function() {
            const tabId = this.dataset.tab;

            // Update buttons
            tabButtons.forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');

            // Update panels
            tabPanels.forEach(panel => panel.classList.remove('active'));
            document.getElementById(tabId).classList.add('active');
        });
    });

    // Special offer countdown
    const offerTimer = document.getElementById('offer-timer');
    const specialOffer = document.getElementById('special-offer');
    const closeOffer = document.getElementById('close-offer');

    let timeLeft = 600; // 10 minutes in seconds

    const countdown = setInterval(function() {
        const minutes = Math.floor(timeLeft / 60);
        const seconds = timeLeft % 60;

        offerTimer.textContent = `Offer ends in ${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;

        if (timeLeft <= 0) {
            clearInterval(countdown);
            specialOffer.style.display = 'none';
        } else {
            timeLeft--;
        }
    }, 1000);

    closeOffer.addEventListener('click', function() {
        specialOffer.style.display = 'none';
        clearInterval(countdown);
    });

    // Form validation
    const messageForm = document.getElementById('message-form');
    const formFeedback = document.getElementById('form-feedback');

    // Real-time validation
    document.getElementById('name').addEventListener('input', validateName);
    document.getElementById('email').addEventListener('input', validateEmail);
    document.getElementById('message').addEventListener('input', validateMessage);
    document.getElementById('password').addEventListener('input', validatePassword);

    function validateName() {
        const nameInput = document.getElementById('name');
        const nameError = document.getElementById('name-error');

        if (nameInput.value.trim() === '') {
            nameInput.classList.add('error');
            nameError.textContent = 'Name is required';
            nameError.style.display = 'block';
            return false;
        } else {
            nameInput.classList.remove('error');
            nameInput.classList.add('valid');
            nameError.style.display = 'none';
            return true;
        }
    }

    function validateEmail() {
        const emailInput = document.getElementById('email');
        const emailError = document.getElementById('email-error');
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        if (emailInput.value.trim() === '') {
            emailInput.classList.add('error');
            emailError.textContent = 'Email is required';
            emailError.style.display = 'block';
            return false;
        } else if (!emailRegex.test(emailInput.value)) {
            emailInput.classList.add('error');
            emailError.textContent = 'Please enter a valid email';
            emailError.style.display = 'block';
            return false;
        } else {
            emailInput.classList.remove('error');
            emailInput.classList.add('valid');
            emailError.style.display = 'none';
            return true;
        }
    }

    function validateMessage() {
        const messageInput = document.getElementById('message');
        const messageError = document.getElementById('message-error');

        if (messageInput.value.trim() === '') {
            messageInput.classList.add('error');
            messageError.textContent = 'Message is required';
            messageError.style.display = 'block';
            return false;
        } else {
            messageInput.classList.remove('error');
            messageInput.classList.add('valid');
            messageError.style.display = 'none';
            return true;
        }
    }

    function validatePassword() {
        const passwordInput = document.getElementById('password');
        const password = passwordInput.value;

        // Only validate if there's a password (it's optional)
        if (password.length > 0) {
            const hasMinLength = password.length >= 8;
            const hasNumber = /\d/.test(password);
            const hasLetter = /[a-zA-Z]/.test(password);

            document.getElementById('length-rule').classList.toggle('valid', hasMinLength);
            document.getElementById('number-rule').classList.toggle('valid', hasNumber);
            document.getElementById('letter-rule').classList.toggle('valid', hasLetter);

            return hasMinLength && hasNumber && hasLetter;
        }
        return true;
    }

    messageForm.addEventListener('submit', function(e) {
        e.preventDefault();

        const isNameValid = validateName();
        const isEmailValid = validateEmail();
        const isMessageValid = validateMessage();
        const isPasswordValid = validatePassword();

        if (isNameValid && isEmailValid && isMessageValid && isPasswordValid) {
            const name = document.getElementById('name').value;
            const email = document.getElementById('email').value;

            // Simulate form submission
            setTimeout(function() {
                formFeedback.textContent = `Thank you, ${name}! We've received your message and will contact you at ${email} soon.`;
                formFeedback.className = 'success';
                messageForm.reset();

                // Clear validation states
                document.querySelectorAll('input, textarea').forEach(input => {
                    input.classList.remove('valid', 'error');
                });

                // Clear password rules highlights
                document.querySelectorAll('.password-rules .rule').forEach(rule => {
                    rule.classList.remove('valid');
                });

                // Clear feedback after 5 seconds
                setTimeout(function() {
                    formFeedback.textContent = '';
                    formFeedback.className = '';
                }, 5000);
            }, 1000);
        } else {
            formFeedback.textContent = 'Please fix the errors in the form';
            formFeedback.className = 'error';
        }
    });

    // Dynamic hero title
    const heroTitle = document.getElementById('hero-title');
    const originalHeroTitle = heroTitle.textContent;

    setInterval(function() {
        if (heroTitle.textContent === originalHeroTitle) {
            heroTitle.textContent = "100% Natural, No Preservatives";
            document.getElementById('hero-subtitle').textContent = "Pure Kenyan dairy at its best";
        } else {
            heroTitle.textContent = originalHeroTitle;
            document.getElementById('hero-subtitle').textContent = "Direct from farm to your table in Nairobi";
        }
    }, 5000);

    // Secret button (long press)
    const secretBtn = document.getElementById('secret-btn');
    const secretMessage = document.getElementById('secret-message');
    let pressTimer;

    secretBtn.addEventListener('mousedown', function() {
        pressTimer = setTimeout(function() {
            secretMessage.textContent = "🎉 Secret Offer: Use code 'MILKLOVER' for 10% off your next purchase!";
            secretMessage.style.display = 'block';

            // Hide after 5 seconds
            setTimeout(function() {
                secretMessage.style.display = 'none';
            }, 5000);
        }, 2000); // 2 second long press
    });

    secretBtn.addEventListener('mouseup', function() {
        clearTimeout(pressTimer);
    });

    secretBtn.addEventListener('mouseleave', function() {
        clearTimeout(pressTimer);
    });

    // Double click logo to change color
    const logoImage = document.getElementById('logo-image');
    const colors = ['#4CAF50', '#2196F3', '#9C27B0', '#FF9800', '#E91E63'];
    let colorIndex = 0;

    logoImage.addEventListener('dblclick', function() {
        colorIndex = (colorIndex + 1) % colors.length;
        this.style.border = `3px solid ${colors[colorIndex]}`;
        this.style.borderRadius = '50%';
    });

    // Keypress detection
    const keypressIndicator = document.createElement('div');
    keypressIndicator.className = 'keypress-indicator';
    document.body.appendChild(keypressIndicator);

    document.addEventListener('keydown', function(e) {
        keypressIndicator.textContent = `Key pressed: ${e.key}`;
        keypressIndicator.style.display = 'block';

        setTimeout(function() {
            keypressIndicator.style.display = 'none';
        }, 1000);
    });

    // Animation for social icons
    const socialIcons = document.querySelectorAll('.social-icon');
    socialIcons.forEach(icon => {
        icon.addEventListener('mouseenter', function() {
            this.style.transition = 'transform 0.3s ease';
            this.style.transform = 'scale(1.2) rotate(10deg)';
        });

        icon.addEventListener('mouseleave', function() {
            this.style.transform = 'scale(1) rotate(0)';
        });
    });
});